package Basics;

public class ClassL {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		float f=3.141592653589793f;
		System.out.println(f);
		System.out.format("Value of f is %f",f);
		System.out.format("Value of f is %1.1f\n",f);
		System.out.format("Value of f is %1.2f\n",f);
		System.out.format("Value of f is %1.3f\n",f);
		System.out.format("Value of f is %1.4f\n",f);
		System.out.format("Value of f is %1.5f\n",f);
		System.out.format("Value of f is %1.6f\n",f);
		System.out.format("Value of f is %1.7f\n",f);
		System.out.format("Value of f is %1.8f\n",f);
		System.out.format("Value of f is %1.9f\n",f);
		
	}

}
